import CookieCard from '../CookieCard/CookieCard';
import Loader from '../Components/Loader/Loader';
import CategoryField from '../Components/CategoryField/CategoryField';
import {connect} from 'react-redux';
import axios from 'axios';
import $ from 'jquery';
import {ScrollSpy} from 'bootstrap';

import {useState, useEffect, Fragment, createRef} from 'react';

function Cards(props){
    const getCookiesUrl = 'http://localhost:3001/cookie/getAll';

    let [cookies, setCookies] = useState([]);
    let [isShowLoader, setShowLoader] = useState(true);
    let refDivScrollSpy = createRef();
    useEffect(()=>{
        axios.get(getCookiesUrl, {
            timeout: 5000
        })
        .then((res)=>{
            setCookies(res.data);
            setShowLoader(false);
        })
        .catch((e)=>{
            console.log(e.message);
            setShowLoader(false);
        });
    }, [props]);
    useEffect(()=>{
        if(refDivScrollSpy.current){
            var scrollSpy = new ScrollSpy(document.body, {
                target: '#categoryField',
                offset: props.categoryFieldHeight
            })
        }
    },[props, refDivScrollSpy]);
    

    let cookieCardGroups = props.categories.map((category)=>{
        let cookieCardsFilteredArr = cookies.filter((cookie)=> cookie.CATEGORY === category);
        let cookieCards = cookieCardsFilteredArr.map((cookie)=>{
            return(
                <CookieCard cookieName= {cookie.NAME} description= {cookie.DESCRIPTION} price= {cookie.PRICE_RS} id= {cookie._id} key={cookie._id}/>
            );
        });
        return(
            <Fragment key={category}>
                <h3 id={category.replace(/\s+/g, '').trim()}>{category}</h3>
                <hr/>
                <div className= 'card-group d-flex justify-content-evenly flex-wrap'>
                    {cookieCards}
                </div>
            </Fragment>
        );
    });

    if(isShowLoader){
        return(
            <Loader coverScreen= {false}/>
        );
    }
    else{
        return(
            <div ref= {refDivScrollSpy} className= "position-relative" tabIndex="0" style={{top: props.categoryFieldHeight}}>
                {cookieCardGroups}
            </div>
        );
    }
}

function mapStateToProps(state){
    return{
        categoryFieldHeight: state.globalStyle.categoryFieldHeight
    }
}

export default connect(mapStateToProps)(Cards);
